import React, { useEffect, useState } from "react";
import {
  BarChart, Bar, PieChart, Pie, Cell, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from "recharts";

function Analytics() {
  const [stats, setStats] = useState({
    jobsPerCompany: [],
    jobsPerLocation: [],
    applicantsPerJob: []
  });

  useEffect(() => {
    fetch("http://localhost:5000/api/analytics")
      .then(res => res.json())
      .then(data => setStats(data));
  }, []);

  const COLORS = ["#8884d8", "#82ca9d", "#ffc658", "#ff8042"];

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-6">Job Portal Analytics</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        
        {/* Jobs per Company */}
        <div className="bg-white shadow rounded p-4">
          <h3 className="text-lg font-semibold mb-4">Jobs Per Company</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={stats.jobsPerCompany}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="company" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="count" fill="#8884d8" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Jobs per Location */}
        <div className="bg-white shadow rounded p-4">
          <h3 className="text-lg font-semibold mb-4">Jobs Per Location</h3>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie
                data={stats.jobsPerLocation}
                dataKey="count"
                nameKey="location"
                cx="50%"
                cy="50%"
                outerRadius={100}
                fill="#82ca9d"
                label
              >
                {stats.jobsPerLocation.map((_, index) => (
                  <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                ))}
              </Pie>
              <Tooltip />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Applicants per Job */}
        <div className="bg-white shadow rounded p-4 col-span-1 md:col-span-2">
          <h3 className="text-lg font-semibold mb-4">Applicants Per Job</h3>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={stats.applicantsPerJob}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="jobTitle" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="applicants" fill="#ffc658" />
            </BarChart>
          </ResponsiveContainer>
        </div>

      </div>
    </div>
  );
}

export default Analytics;
